import { Streamlit } from "streamlit-component-lib"
import { useRenderData } from "streamlit-component-lib-react-hooks"
import React, { useState, useCallback } from "react"
import { AddressAutofill } from "@mapbox/search-js-react"

/**
 * This is a React-based component template with functional component and hooks.
 */
const MyForm: React.VFC = () => {
  // "useRenderData" returns the renderData passed from Python.
  const renderData = useRenderData()

  // const token =
  //   "pk.eyJ1IjoiYWxpY2Vtc2FwaWVudCIsImEiOiJjbDJ5MHBtZ3MxMHk0M25wZmFnaWdiaWx4In0.aTDpzRDIUG50F-Iopnhb4Q"

  const defaultAddress = {
    address: "",
    unit: "",
    city: "",
    state: "",
    country: "",
    postcode: "",
  }
  const [address, setAddress] = useState(defaultAddress)
  const [isFocused, setIsFocused] = useState(false)

  /** Click handler for our "Click Me!" button. */
  const handleInputChange = useCallback(
    (e) => {
      const { name, value } = e.target
      setAddress({
        ...address,
        [name]: value,
      })
    },
    [address]
  )

  const handleSubmit = useCallback(
    (e) => {
      e.preventDefault()
      console.log(address)
      Streamlit.setComponentValue(address)
    },
    [address]
  )

  /** Focus handler for our "Click Me!" button. */
  const onFocus = useCallback(() => {
    setIsFocused(true)
  }, [])

  /** Blur handler for our "Click Me!" button. */
  const onBlur = useCallback(() => {
    setIsFocused(false)
  }, [])

  // Arguments that are passed to the plugin in Python are accessible
  // via `renderData.args`. Here, we access the "name" arg.
  const name = renderData.args["name"]

  return (
    <div>
      Hello, {name}! &nbsp;
      <form onSubmit={(e) => handleSubmit(e)}>
        {/* <AddressAutofill accessToken={token}> */}
        <input
          name="address"
          value={address.address}
          placeholder="Address"
          type="text"
          autoComplete="address-line1"
          onChange={handleInputChange}
        />
        {/* </AddressAutofill> */}
        <input
          name="unit"
          value={address.unit}
          placeholder="Unit number"
          type="text"
          autoComplete="address-line2"
          onChange={handleInputChange}
        />
        <input
          name="city"
          value={address.city}
          placeholder="City"
          type="text"
          autoComplete="address-level2"
          onChange={handleInputChange}
        />
        <input
          name="state"
          value={address.state}
          placeholder="State"
          type="text"
          autoComplete="address-level1"
          onChange={handleInputChange}
        />
        <input
          name="country"
          value={address.country}
          placeholder="Country"
          type="text"
          autoComplete="country"
          onChange={handleInputChange}
        />
        <input
          name="postcode"
          value={address.postcode}
          placeholder="Postcode"
          type="text"
          autoComplete="postal-code"
          onChange={handleInputChange}
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  )
}

// export default withStreamlitConnection(MyForm)
export default MyForm
