  <div class="MapboxAddressMinimap" aria-hidden="true">
    <div class="MinimapImageContainer">
      <img class="MinimapImage" draggable="false"></img>
      <div class="MinimapInnerFrame">
        <div class="MinimapMarker"></div>
        <div class="MinimapAttribution">
          <div class="MinimapAttributionLogo">
            <a target="_blank" rel="noopener nofollow" href="https://www.mapbox.com/" aria-label="Mapbox logo">
              ${logo}
            </a>
          </div>
          <div class="MinimapAttributionText">
            <a target="_blank" href='https://www.mapbox.com/about/maps/'> Mapbox</a><a target="_blank" href='http://www.openstreetmap.org/copyright'> OpenStreetMap</a>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>
`);

const STYLE_TOGGLE_TEMPLATE = createElementFromString<HTMLTemplateElement>(`
<template>
  <button type="button" class="MinimapStyleToggle"></button>
</template>
`);

const FOOTER_TEMPLATE = createElementFromString<HTMLTemplateElement>(`
<template>
  <div class="MinimapFooter">Adjust the marker on the map if it doesn't precisely match your location. This helps your delivery reach you faster.</div>
</template>
`);

const EDIT_BUTTONS_TEMPLATE = createElementFromString<HTMLTemplateElement>(`
<template>
  <div class="MinimapEditButtons">
    <div class="Button ButtonPrimary MinimapButtonAdjust">Adjust pin</div>
    <div class="Button ButtonPrimary MinimapButtonSave" aria-hidden="true">Save</div>
    <div class="Button MinimapButtonCancel" aria-hidden="true">Cancel</div>
  </div>
</template>
`);

type Binding = {
  MapboxAddressMinimap: HTMLElement;
  ImageContainer: HTMLElement;
  Image: HTMLImageElement;
  Marker: HTMLElement;
  MapStyleToggle?: HTMLElement;
  EditButtons?: HTMLElement;
  ButtonAdjust?: HTMLButtonElement;
  ButtonSave?: HTMLButtonElement;
  ButtonCancel?: HTMLButtonElement;
};

/**
 * `MapboxAddressMinimap`, also available as the element `<mapbox-address-minimap>`,
 * is a component that displays a marker for confirmation purposes.
 *
 * Optionally, this marker is editable. When editable, the marker can be moved
 * around the map and the updated location is sent back to the Mapbox Contribute
 * workflow.
 *
 * The goal of `MapboxAddressMinimap` is to reduce delivery error in shipping and
 * local dispatching contexts.
 *
 * `MapboxAddressMinimap` expands to fill its container, and is hidden unless
 * {@link MapboxAddressMinimap#feature} is truthy. Setting {@link MapboxAddressMinimap#feature}
 * to `null` hides the component.
 *
 * @class MapboxAddressMinimap
 * @example
 * ```typescript
 * const minimap = new MapboxAddressMinimap();
 * containerElement.appendChild(minimap);
 * minimap.feature = {
 *   type: 'Feature',
 *   geometry: {
 *     type: 'Point',
 *     coordinates: [-122.4194, 37.7749]
 *   },
 *   properties: {}
 * };
 * ```
 */
export class MapboxAddressMinimap extends HTMLScopedElement {
  static observedAttributes: string[] = [
    'access-token',
    'can-adjust-marker',
    'keep-marker-centered',
    'marker-anchor',
    'satellite-toggle'
  ];

  #canAdjustMarkerInternal = false;

  /** @section {Markers} */

  /**
   * If `true`, the marker can be moved around the map. Defaults to `false`.
   *
   * When editable, the marker can be moved around the map and the updated
   * location can be referenced from the {@link MapboxAddressMinimap#onSaveMarkerLocation} callback.
   *
   * @name canAdjustMarker
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {boolean}
   */
  get canAdjustMarker(): boolean {
    return this.#canAdjustMarkerInternal;
  }
  set canAdjustMarker(val: boolean) {
    this.#canAdjustMarkerInternal = val;
    val ? this.#addMarkerEditControls() : this.#removeMarkerEditControls();
  }
  /**
   * If `true`, the map when panned moves around the marker, keeping the marker
   * centered. Defaults to `false`.
   *
   * @name keepMarkerCentered
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {boolean}
   */
  keepMarkerCentered = false;
  /**
   * The anchor of the marker, relative to center of the expanded size. Defaults to `'bottom'`.
   *
   * @name markerAnchor
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {Anchor}
   */
  markerAnchor: Anchor = 'bottom';
  /**
   * A client-defined callback that is triggered when the "Save" button is clicked in the editing interface,
   * and gives access to the adjusted marker coordinate.
   *
   * @name onSaveMarkerLocation
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {function}
   */
  onSaveMarkerLocation: (coordinate: [number, number]) => void;

  #isAdjustMarkerEditing = false;

  #imageLoaded = false;

  #feature: GeoJSON.Feature<GeoJSON.Point>;
  #url = '';

  #width: number;
  #height: number;

  #binding: Binding;

  #markerController: MarkerController;

  #accessToken: string;

  /** @section {Input data} */

  /**
   * The [Mapbox access token](https://docs.mapbox.com/help/glossary/access-token/) to use for all requests.
   *
   * If not explicitly set on the component, this will reference the value in the global config object.
   *
   * @name accessToken
   * @instance
   * @memberof MapboxAddressMinimap
   * @example
   * ```typescript
   * minimap.accessToken = 'pk.my-mapbox-access-token';
   * ```
   */
  get accessToken(): string {
    return this.#accessToken || config.accessToken;
  }
  set accessToken(newToken: string) {
    this.#accessToken = newToken;
  }

  /**
   * A [GeoJSON](https://docs.mapbox.com/help/glossary/geojson/) Feature representing
   * a [Point](https://geojson.org/geojson-spec.html#point) geometry.
   *
   * The minimap is hidden unless
   * {@link MapboxAddressMinimap#feature} is truthy. Setting {@link MapboxAddressMinimap#feature}
   * to `null` hides the component.
   *
   * @name feature
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {Feature}
   */
  get feature(): GeoJSON.Feature<GeoJSON.Point> {
    return this.#feature;
  }
  set feature(feature: GeoJSON.Feature<GeoJSON.Point>) {
    this.#feature = feature;

    if (!feature) {
      this.hide();
    } else {
      this.show();
    }
  }

  get template(): HTMLTemplateElement {
    return TEMPLATE;
  }

  get templateStyle(): string {
    return style;
  }

  get templateUserStyle(): string {
    return getThemeCSS('.MapboxAddressMinimap', this.theme);
  }

  #themeInternal: Theme = {};
  #satelliteToggleInternal = false;

  /** @section {Appearance} */

  /**
   * If `true`, the map will have an image toggle between Map and Satellite styles.
   *
   * @name satelliteToggle
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {boolean}
   */
  get satelliteToggle(): boolean {
    return this.#satelliteToggleInternal;
  }
  set satelliteToggle(val: boolean) {
    this.#satelliteToggleInternal = val;
    val ? this.#addSatelliteToggle() : this.#removeSatelliteToggle();
  }

  /**
   * The {@link Theme} to use for styling the editing interface.
   *
   * @name theme
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {Theme}
   * @example
   * ```typescript
   * autofill.theme = {
   *   variables: {
   *     colorPrimary: 'myBrandRed'
   *   }
   * };
   * ```
   */
  get theme(): Theme {
    return this.#themeInternal;
  }
  set theme(theme: Theme) {
    this.#themeInternal = theme;

    if (!this.#binding || !theme) {
      return;
    }

    this.updateTemplateUserStyle(getThemeCSS('.MapboxAddressMinimap', theme));

    const { Marker, MapStyleToggle } = this.#binding;
    Marker.innerHTML = getIcon('marker', theme);
    if (MapStyleToggle) {
      MapStyleToggle.style.backgroundImage = this.#getToggleBackgroundImageUrl(
        this.mapStyleMode === 'default' ? 'satellite' : 'default'
      );
    }
  }

  #mapStyleMode: MapStyleMode = 'default';

  /**
   * The map style to use, either `'default'` or `'satellite'`. The default map
   * style is configurable with {@link MapboxAddressMinimap#defaultMapStyle}.
   *
   * @name mapStyleMode
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {'default' | 'satellite'}
   */
  get mapStyleMode(): MapStyleMode {
    return this.#mapStyleMode;
  }
  set mapStyleMode(styleMode: MapStyleMode) {
    const prevStyleMode = this.#mapStyleMode;
    this.#mapStyleMode = styleMode;
    if (!this.#binding) {
      return;
    }

    // Update toggle button background image
    const { MapStyleToggle } = this.#binding;
    if (!MapStyleToggle) {
      return;
    }

    MapStyleToggle.style.backgroundImage =
      this.#getToggleBackgroundImageUrl(prevStyleMode);
    // Update title attribute
    MapStyleToggle.setAttribute(
      'title',
      `Switch to ${prevStyleMode === 'satellite' ? 'Satellite' : 'Default'}`
    );
    this.#updateImageSrc();
  }

  #defaultMapStyle: [string, string] = ['mapbox', 'streets-v11'];

  /**
   * The map style to use for the default map style. Defaults to `['mapbox', 'streets-v11']`.
   *
   * @name defaultMapStyle
   * @instance
   * @memberof MapboxAddressMinimap
   * @type {[string, string]}
   */
  get defaultMapStyle(): [string, string] {
    return this.#defaultMapStyle;
  }
  set defaultMapStyle(style: [string, string]) {
    this.#defaultMapStyle = style;
    this.#updateImageSrc();
  }

  /**
   * @deprecated Use `container` instead.
   */
  #container: HTMLElement | null;

  get container(): HTMLElement | null {
    return this.#container;
  }

  set container(newContainer: HTMLElement | null) {
    if (newContainer) {
      newContainer.style.position = 'relative';
      this.#container = newContainer;
    }
  }

  /** @section {Methods} */

  /**
   * If {@link MapboxAddressMinimap#feature} is truthy, show the minimap.
   */
  show(): void {
    if (!this.#feature) {
      return;
    }

    const lngLat = this.#feature.geometry.coordinates;
    this.#markerController.coordinate = lngLat as [number, number];

    this.#url = this.#getImageUrl(lngLat as [number, number]);

    // Update image.
    const { MapboxAddressMinimap, Image } = this.#binding;
    Image.src = this.#url;

    MapboxAddressMinimap.removeAttribute('aria-hidden');
  }

  /**
   * Hide the minimap.
   */
  hide(): void {
    const { MapboxAddressMinimap } = this.#binding;
    MapboxAddressMinimap.setAttribute('aria-hidden', 'true');
  }

  #toggleMarkerEditing = (): void => {
    const { ImageContainer, ButtonAdjust, ButtonSave, ButtonCancel } =
      this.#binding;
    if (this.#isAdjustMarkerEditing) {
      ImageContainer.classList.add(`${this.dataset.seed}--draggable`);
      this.#markerController.isActive = true;
      ButtonAdjust.setAttribute('aria-hidden', 'true');
      ButtonSave.removeAttribute('aria-hidden');
      ButtonCancel.removeAttribute('aria-hidden');
    } else {
      ImageContainer.classList.remove(`${this.dataset.seed}--draggable`);
      this.#markerController.isActive = false;
      ButtonAdjust.removeAttribute('aria-hidden');
      ButtonSave.setAttribute('aria-hidden', 'true');
      ButtonCancel.setAttribute('aria-hidden', 'true');
    }
  };

  #handleStartMarkerEditing = (): void => {
    this.#isAdjustMarkerEditing = true;
    this.#toggleMarkerEditing();
  };

  #handleSaveMarkerEditing = (): void => {
    // Send feedback to Contribute API if feature includes address info, and the marker location has changed
    if (
      this.feature.properties.full_address &&
      !deepEquals(
        this.feature.geometry.coordinates,
        this.#markerController.coordinate
      )
    ) {
      const [lng, lat] = this.#markerController.coordinate;
      sendFeedback(this.accessToken, {
        originalCoordinate: this.feature.geometry.coordinates as [
          number,
          number
        ],
        originalAddress: this.feature.properties.full_address,
        changes: {
          location: { longitude: lng, latitude: lat }
        }
      });
    }
    // Client-defined callback
    if (this.onSaveMarkerLocation) {
      this.onSaveMarkerLocation(this.#markerController.coordinate);
    }
    // Update component state
    this.#isAdjustMarkerEditing = false;
    this.#toggleMarkerEditing();
  };

  #handleCancelMarkerEditing = (): void => {
    this.#markerController.reCenter();
    this.#isAdjustMarkerEditing = false;
    this.#toggleMarkerEditing();
  };

  #handleToggleMapStyle = (): void => {
    this.mapStyleMode =
      this.mapStyleMode === 'default' ? 'satellite' : 'default';
  };

  #handleImageLoad = (): void => {
    if (!this.#imageLoaded) {
      this.#markerController.reCenter();
    }
    this.#imageLoaded = true;
  };

  #handleImageError = (): void => {
    this.#imageLoaded = false;
  };

  /**
   * Get the Static Image API URL for a given coordinate.
   * Attempts to get an image twice as large as the container to enable panning,
   * while constraining to the static API size limits.
   * @param lngLatLike - The coordinate representing the center of the image
   * @returns
   */
  #getImageUrl = (lngLatLike: LngLatLike): string => {
    // Return empty string if height or width is 0 to prevent 422 error
    if (this.#width === 0 || this.#height === 0) return '';

    const [username, styleId] = this.defaultMapStyle;
    const defaultBaseUrl = getStaticBaseUrl(username, styleId);
    const baseUrl =
      this.mapStyleMode === 'default'
        ? defaultBaseUrl
        : STATIC_BASE_URL_SATELLITE;
    const skuToken =
      AUTOFILL_SKU_TOKEN_PREFIX + config.autofillSessionToken.toString();
    let imgUrl =
      baseUrl +
      LngLat.convert(lngLatLike).toArray().join(',') +
      ',' +
      ZOOM +
      ',0/' +
      Math.min(this.#width * 2, MAX_IMAGE_DIM) +
      'x' +
      Math.min(this.#height * 2, MAX_IMAGE_DIM) +
      '?access_token=' +
      this.accessToken +
      '&attribution=false' +
      '&logo=false';
    if (config.autofillSessionEnabled) {
      imgUrl += `&sku=${skuToken}`;
    }
    return imgUrl;
  };

  #updateImageSrc = (): void => {
    if (this.#feature) {
      const lngLat = this.#feature.geometry.coordinates;
      this.#url = this.#getImageUrl(lngLat as [number, number]);
      const { Image } = this.#binding;
      Image.src = this.#url;
    }
  };

  #getToggleBackgroundImageUrl = (styleMode: MapStyleMode): string => {
    return `url("${getImage(
      styleMode === 'default' ? 'styleToggleDefault' : 'styleToggleSatellite',
      this.theme
    )}")`;
  };

  #setSize = (): void => {
    const { MapboxAddressMinimap, ImageContainer } = this.#binding;
    const { width, height } = getElementSize(this.container);
    this.#width = Math.min(width, MAX_IMAGE_DIM); // Constrain to Static Image API limit
    this.#height = Math.min(height, MAX_IMAGE_DIM); // Constrain to Static Image API limit
    MapboxAddressMinimap.style.setProperty('width', `${this.#width}px`);
    ImageContainer.style.setProperty('height', `${this.#height}px`);
    ImageContainer.style.setProperty('width', `${this.#width}px`);
    // Update the image source URL based on the new size
    this.#updateImageSrc();
  };

  #addMarkerEditControls = (): void => {
    // Footer
    const existingFooter = this.querySelector('.MinimapFooter');
    if (existingFooter) return;
    const footerElement = this.prepareTemplate(FOOTER_TEMPLATE);
    const minimapElement = this.querySelector('.MapboxAddressMinimap');
    if (!minimapElement) return;
    minimapElement.appendChild(footerElement);

    // Edit buttons
    const existingEditBtns = this.querySelector('.MinimapEditButtons');
    if (existingEditBtns) return;
    const editButtonsElement = this.prepareTemplate(EDIT_BUTTONS_TEMPLATE);
    const innerFrame = this.querySelector('.MinimapInnerFrame');
    innerFrame.appendChild(editButtonsElement);

    // Add to or update object binding
    this.#binding = {
      ...this.#binding,
      EditButtons: this.querySelector('.MinimapEditButtons'),
      ButtonAdjust: this.querySelector('.MinimapButtonAdjust'),
      ButtonSave: this.querySelector('.MinimapButtonSave'),
      ButtonCancel: this.querySelector('.MinimapButtonCancel')
    };

    // Bind callbacks to button click events
    const { ButtonAdjust, ButtonSave, ButtonCancel } = this.#binding;
    ButtonAdjust.addEventListener('click', this.#handleStartMarkerEditing);
    ButtonSave.addEventListener('click', this.#handleSaveMarkerEditing);
    ButtonCancel.addEventListener('click', this.#handleCancelMarkerEditing);
  };

  #removeMarkerEditControls = (): void => {
    if (!this.#binding) return;
    const { EditButtons, ButtonAdjust, ButtonSave, ButtonCancel } =
      this.#binding;

    const existingFooter = this.querySelector('.MinimapFooter');
    existingFooter?.remove();
    EditButtons?.remove();
    if (ButtonAdjust) {
      ButtonAdjust.remove();
      ButtonAdjust.removeEventListener('click', this.#handleStartMarkerEditing);
    }
    if (ButtonSave) {
      ButtonSave.remove();
      ButtonSave.removeEventListener('click', this.#handleSaveMarkerEditing);
    }
    if (ButtonCancel) {
      ButtonCancel.remove();
      ButtonCancel.removeEventListener(
        'click',
        this.#handleCancelMarkerEditing
      );
    }

    delete this.#binding.EditButtons;
    delete this.#binding.ButtonAdjust;
    delete this.#binding.ButtonSave;
    delete this.#binding.ButtonCancel;
  };

  #addSatelliteToggle = (): void => {
    const existingToggle = this.querySelector('.MinimapStyleToggle');
    if (existingToggle) return;

    // Add element to DOM
    const toggleElement = this.prepareTemplate(STYLE_TOGGLE_TEMPLATE);
    const innerFrame = this.querySelector('.MinimapInnerFrame');
    if (!innerFrame) return;
    innerFrame.appendChild(toggleElement);

    // Add to or update object binding
    this.#binding.MapStyleToggle = toggleElement;

    // Bind callback
    toggleElement.addEventListener('click', this.#handleToggleMapStyle);
    // Initialize background image
    toggleElement.style.backgroundImage = this.#getToggleBackgroundImageUrl(
      this.mapStyleMode === 'default' ? 'satellite' : 'default'
    );
    toggleElement.setAttribute(
      'title',
      `Switch to ${this.mapStyleMode === 'default' ? 'Satellite' : 'Default'}`
    );
  };

  #removeSatelliteToggle = (): void => {
    if (!this.#binding) return;
    const { MapStyleToggle } = this.#binding;
    if (!MapStyleToggle) return;

    MapStyleToggle.remove();
    MapStyleToggle.removeEventListener('click', this.#handleToggleMapStyle);

    delete this.#binding.MapStyleToggle;
  };

  connectedCallback(): void {
    super.connectedCallback();

    this.#binding = bindElements<Binding>(this, {
      MapboxAddressMinimap: '.MapboxAddressMinimap',
      ImageContainer: '.MinimapImageContainer',
      Image: '.MinimapImage',
      Marker: '.MinimapMarker',
      MapStyleToggle: '.MinimapStyleToggle',
      EditButtons: '.MinimapEditButtons',
      ButtonAdjust: '.MinimapButtonAdjust',
      ButtonSave: '.MinimapButtonSave',
      ButtonCancel: '.MinimapButtonCancel'
    });

    // Update toggle button background image.
    this.mapStyleMode = this.#mapStyleMode;

    // Initialize theme if not set before connectedCallback
    this.theme = { ...this.theme };

    // Append buttons and footer if adjustable
    if (this.canAdjustMarker) {
      this.#addMarkerEditControls();
    }

    // Append style toggle if enabled
    if (this.satelliteToggle) {
      this.#addSatelliteToggle();
    }

    // Calculate size and listen for changes
    this.container = this.parentElement;
    const resizeObserver = new ResizeObserver(this.#setSize);
    resizeObserver.observe(this.container);
    this.#setSize();

    const { MapboxAddressMinimap, ImageContainer, Image, Marker } =
      this.#binding;

    // Initialize marker controller
    this.#markerController = new MarkerController(
      ImageContainer,
      Marker,
      this.keepMarkerCentered,
      ZOOM,
      this.markerAnchor
    );
    this.#markerController.reCenter();
    Image.onload = this.#handleImageLoad;
    Image.onerror = this.#handleImageError;

    Image.src = this.#url;

    if (this.#feature) MapboxAddressMinimap.removeAttribute('aria-hidden');
    else MapboxAddressMinimap.setAttribute('aria-hidden', 'true');
  }

  attributeChangedCallback(
    name: string,
    oldValue: string,
    newValue: string
  ): void {
    if (name === 'access-token') {
      this.accessToken = newValue;
    } else if (name === 'can-adjust-marker') {
      this.canAdjustMarker = newValue === 'true';
    } else if (name === 'keep-marker-centered') {
      this.keepMarkerCentered = newValue === 'true';
    } else if (name === 'marker-anchor') {
      const newAnchor = newValue as Anchor;
      this.markerAnchor = newAnchor;
      this.#markerController && (this.#markerController.anchor = newAnchor);
    } else if (name === 'satellite-toggle') {
      this.satelliteToggle = newValue === 'true';
    }
  }
}

declare global {
  interface Window {
    MapboxAddressMinimap: typeof MapboxAddressMinimap;
  }
}

window.MapboxAddressMinimap = MapboxAddressMinimap;

if (!window.customElements.get('mapbox-address-minimap')) {
  customElements.define('mapbox-address-minimap', MapboxAddressMinimap);
}
import SphericalMercator from '@mapbox/sphericalmercator';
import { deepEquals, round } from '.';
import { getElementSize } from './dom';

export type MapStyleMode = 'default' | 'satellite';

/**
 * @typedef Anchor
 */
export type Anchor =
  | 'center'
  | 'top-left'
  | 'top-right'
  | 'bottom-left'
  | 'bottom-right'
  | 'top'
  | 'bottom'
  | 'left'
  | 'right';

// eslint-disable-next-line
// @ts-ignore
const merc = new SphericalMercator({ size: 512, antimeridian: true });

export const MAX_IMAGE_DIM = 1280;

interface MarkerTransform {
  anchorX: number;
  anchorY: number;
  globalX: number;
  globalY: number;
}

/**
 * Get the [X,Y] pixel offset for a given SVG marker and anchor definition
 * @param marker - An SVG element used as the marker
 * @param anchor - The anchor point of the marker to put closest to the marked coordinate
 * @returns [X,Y] pixel coordinate
 */
export function getAnchorOffset(
  marker: HTMLElement,
  anchor: Anchor
): [number, number] {
  const { width, height } = getElementSize(marker, true);
  switch (anchor) {
    case 'center':
      return [0, 0];
    case 'top':
      return [0, height / 2];
    case 'bottom':
      return [0, (-1 * height) / 2];
    case 'left':
      return [width / 2, 0];
    case 'right':
      return [(-1 * width) / 2, 0];
    case 'top-left':
      return [width / 2, height / 2];
    case 'top-right':
      return [(-1 * width) / 2, height / 2];
    case 'bottom-left':
      return [width / 2, (-1 * height) / 2];
    case 'bottom-right':
      return [(-1 * width) / 2, (-1 * height) / 2];
  }
}

/**
 * Manages positioning of a static map image and marker relative to
 * pixel and geographical coordinates. Handles pointer events for
 * interactive/adjustable instances.
 */
export class MarkerController {
  imgContainerElement: HTMLElement;
  markerElement: HTMLElement;
  keepMarkerCentered: boolean;
  zoom: number;

  /** X-offset for the marker element to account for its positioning anchor */
  anchorOffsetX: number;
  /** Y-offset for the marker element to account for its positioning anchor */
  anchorOffsetY: number;

  /** Current X pixel of the pointer on the page */
  curPointerXPos: number;
  /** Current Y pixel of the pointer on the page */
  curPointerYPos: number;
  /** X-pixels deviation of marker position from the current map/feature center point */
  markerDeltaX: number;
  /** Y-pixels deviation of marker position from the current map/feature center point */
  markerDeltaY: number;

  /** Lng/lat of image center represented as screen pixels */
  imgCenterPx: [number, number];
  /** Lng/lat of adjusted image center as screen pixels */
  imgCenterAdjustedPx: [number, number];

  /**
   *
   * @param imageContainer - The parent element of the <img>
   * @param marker - SVG Element used as the map marker
   * @param keepMarkerCentered - If true, the marker will stay centered within the frame while the map moves behind it
   * @param zoom - The zoom level of the static map, used to calculate between screen pixel and lng/lat
   * @param anchor - The part of the marker that should be positioned closest to the coordinate
   */
  constructor(
    imageContainer: HTMLElement,
    marker: HTMLElement,
    keepMarkerCentered: boolean,
    zoom: number,
    anchor: Anchor
  ) {
    this.markerElement = marker;
    this.imgContainerElement = imageContainer;
    this.keepMarkerCentered = keepMarkerCentered;
    this.zoom = zoom;
    this.anchor = anchor;

    this.curPointerXPos = 0;
    this.curPointerYPos = 0;
    this.markerDeltaX = 0;
    this.markerDeltaY = 0;

    this.imgContainerElement.addEventListener(
      'pointerdown',
      this.#onPointerDownImage
    );

    if (!this.keepMarkerCentered) {
      this.markerElement.addEventListener(
        'pointerdown',
        this.#onPointerDownMarker
      );
    }

    // Listen for changes to anchor size
    const resizeObserver = new ResizeObserver(this.#handleAnchorResize);
    resizeObserver.observe(this.markerElement);
  }

  #anchor: Anchor;

  get anchor(): Anchor {
    return this.#anchor;
  }

  set anchor(newAnchor: Anchor) {
    this.#anchor = newAnchor;
    [this.anchorOffsetX, this.anchorOffsetY] = getAnchorOffset(
      this.markerElement,
      newAnchor
    );
    this.markerTransform = {
      anchorX: this.anchorOffsetX,
      anchorY: this.anchorOffsetY
    };
  }

  #handleAnchorResize = (): void => {
    [this.anchorOffsetX, this.anchorOffsetY] = getAnchorOffset(
      this.markerElement,
      this.anchor
    );
    this.markerTransform = {
      anchorX: this.anchorOffsetX,
      anchorY: this.anchorOffsetY
    };
  };

  #markerTransform: MarkerTransform = {
    anchorX: 0,
    anchorY: 0,
    globalX: 0,
    globalY: 0
  };

  get markerTransform(): MarkerTransform {
    return this.#markerTransform;
  }

  set markerTransform(val: Partial<MarkerTransform>) {
    this.#markerTransform = {
      ...this.#markerTransform,
      ...val
    };
    this.#updateMarkerTransform();
  }

  #isActive = false;

  /** True if the marker is currently in edit mode */
  get isActive(): boolean {
    return this.#isActive;
  }

  /** Set to true to enable editing of the marker */
  set isActive(val: boolean) {
    this.imgContainerElement.style.touchAction = val ? 'none' : '';
    this.#isActive = val;
  }

  /** Height of the <img> element */
  get imgHeight(): number {
    return Math.min(this.imgContainerElement.clientHeight * 2, MAX_IMAGE_DIM);
  }

  /** Width of the <img> element */
  get imgWidth(): number {
    return Math.min(this.imgContainerElement.clientWidth * 2, MAX_IMAGE_DIM);
  }

  #originalCoordinate: [number, number];

  /** Lng/lat of the current marker location */
  get coordinate(): [number, number] {
    const adjustedPx: [number, number] = this.keepMarkerCentered
      ? this.imgCenterAdjustedPx
      : [
          this.imgCenterPx[0] - this.markerDeltaX,
          this.imgCenterPx[1] + this.markerDeltaY
        ];

    // If the screen pixel hasn't changed, use the original coordinate so as not to incur any precision rounding during translation
    if (deepEquals(adjustedPx, this.imgCenterPx)) {
      return this.#originalCoordinate;
    } else {
      const lngLat = merc.ll(adjustedPx, this.zoom);
      return [round(lngLat[0], 6), round(lngLat[1], 6)];
    }
  }

  /** Set the lng/lat corresponding to the initial marker and image center location */
  set coordinate(lngLat: [number, number]) {
    this.#originalCoordinate = lngLat;
    this.imgCenterPx = this.imgCenterAdjustedPx = merc.px(lngLat, this.zoom);
  }

  // Marker dragging event handlers
  #onPointerDownMarker = (m: PointerEvent): void => {
    if (!this.isActive) return;

    m.preventDefault();
    m.stopPropagation();

    this.#updatePointerPosition(m);

    window.addEventListener('pointermove', this.#onPointerMoveMarker);
    window.addEventListener('pointerup', this.#onPointerUpMarker);
  };

  #onPointerUpMarker = (): void => {
    window.removeEventListener('pointermove', this.#onPointerMoveMarker);
    window.removeEventListener('pointerup', this.#onPointerUpMarker);
  };

  #onPointerMoveMarker = (m: PointerEvent): void => {
    m.preventDefault();
    m.stopPropagation();

    // Calculate change in pointer position
    const diffX = this.curPointerXPos - m.pageX;
    const diffY = this.curPointerYPos - m.pageY;

    // Update relative marker positioning
    this.markerDeltaX += diffX;
    this.markerDeltaY -= diffY;
    // Clamp to image bounds
    this.markerDeltaX = Math.max(
      Math.min(this.imgWidth / 2, this.markerDeltaX),
      (this.imgWidth / 2) * -1
    );
    this.markerDeltaY = Math.max(
      Math.min(this.imgHeight / 2, this.markerDeltaY),
      (this.imgHeight / 2) * -1
    );

    // Calculate X/Y transforms
    const imageOffsetX = this.imgCenterPx[0] - this.imgCenterAdjustedPx[0];
    const imageOffsetY = this.imgCenterPx[1] - this.imgCenterAdjustedPx[1];
    const deltaX = this.markerDeltaX - imageOffsetX;
    const deltaY = this.markerDeltaY + imageOffsetY;

    this.markerTransform = { globalX: deltaX, globalY: deltaY };

    // Update pointer position
    this.#updatePointerPosition(m);
  };

  // Image dragging event handlers
  #onPointerDownImage = (m: PointerEvent): void => {
    if (!this.isActive) return;

    m.preventDefault();
    m.stopPropagation();

    this.#updatePointerPosition(m);

    window.addEventListener('pointermove', this.#onPointerMoveImage);
    window.addEventListener('pointerup', this.#onPointerUpImage);
  };

  #onPointerUpImage = (): void => {
    window.removeEventListener('pointermove', this.#onPointerMoveImage);
    window.removeEventListener('pointerup', this.#onPointerUpImage);
  };

  #onPointerMoveImage = (m: PointerEvent): void => {
    m.preventDefault();

    // Calculate and constrain updated scroll position
    let top = Math.round(
      this.imgContainerElement.scrollTop + (this.curPointerYPos - m.pageY)
    );
    // Clamp to container bounds
    top = Math.max(
      Math.min(this.imgHeight - this.imgContainerElement.clientHeight, top),
      0
    );
    let left = Math.round(
      this.imgContainerElement.scrollLeft + (this.curPointerXPos - m.pageX)
    );
    // Clamp to container bounds
    left = Math.max(
      Math.min(this.imgWidth - this.imgContainerElement.clientWidth, left),
      0
    );
    this.imgContainerElement.scrollTop = top;
    this.imgContainerElement.scrollLeft = left;

    // Update adjusted center location
    const diffX = Math.round(
      left - (this.imgWidth - this.imgContainerElement.clientWidth) / 2
    );
    const diffY = Math.round(
      (this.imgHeight - this.imgContainerElement.clientHeight) / 2 - top
    );
    this.imgCenterAdjustedPx = [
      this.imgCenterPx[0] + diffX,
      this.imgCenterPx[1] - diffY
    ];

    if (!this.keepMarkerCentered) {
      // Move marker in sync with image to maintain visual positioning on map
      const deltaX = this.markerDeltaX + diffX;
      const deltaY = this.markerDeltaY + diffY;
      this.markerTransform = { globalX: deltaX, globalY: deltaY };
    }

    // Update pointer position
    this.#updatePointerPosition(m);
  };

  #updatePointerPosition = (m: PointerEvent): void => {
    this.curPointerXPos = m.pageX;
    this.curPointerYPos = m.pageY;
  };

  #updateMarkerTransform = (): void => {
    const { anchorX, anchorY, globalX, globalY } = this.#markerTransform;
    const transformX = anchorX - globalX;
    const transformY = anchorY + globalY;
    this.markerElement.style.transform = `translate(calc(-50% + ${transformX}px), calc(-50% + ${transformY}px))`;
  };

  /** Re-center the marker on the map at its original location */
  reCenter = (): void => {
    // Set scroll values to center image in its container
    const top = (this.imgHeight - this.imgContainerElement.clientHeight) / 2;
    const left = (this.imgWidth - this.imgContainerElement.clientWidth) / 2;
    this.imgContainerElement.scrollTop = top;
    this.imgContainerElement.scrollLeft = left;

    // Reset adjusted image center screen coordinate to match original
    this.imgCenterAdjustedPx = this.imgCenterPx;

    // Reset marker transform in case it was changed
    this.markerDeltaX = this.markerDeltaY = 0;
    this.markerTransform = { globalX: 0, globalY: 0 };
  };
}
import {
  AutofillOptions,
  AutofillRetrieveResponse,
  AutofillSuggestionResponse,
  Evented,
  MapboxAutofill,
  SearchSession
} from '@mapbox/search-js-core';

import { MapboxSearchListboxAutofillType } from './components/MapboxAddressAutofill';
import { MapboxSearchListbox } from './components/MapboxSearchListbox';
import { MapboxHTMLEvent } from './MapboxHTMLEvent';
import { Theme } from './theme';
import { deepEquals } from './utils';
import { fillFormWithFeature, findAddressInputs } from './utils/autofill';

import { config } from './config';

/**
 * @class AutofillInstance
 */
export class AutofillInstance {
  #input: HTMLInputElement;
  #collection: AutofillCollectionType;

  listbox: MapboxSearchListboxAutofillType = new MapboxSearchListbox();

  constructor(
    collection: AutofillCollectionType,
    input: HTMLInputElement,
    autofillRef: MapboxAutofill
  ) {
    this.#input = input;
    this.#collection = collection;
    this.listbox.input = this.#input;

    // Bind the listbox to the session.
    this.listbox.session = new SearchSession(autofillRef);
    this.listbox.session.sessionToken = config.autofillSessionToken;

    this.listbox.addEventListener('suggest', this.#handleSuggest);
    this.listbox.addEventListener('suggesterror', this.#handleSuggestError);
    this.listbox.addEventListener('retrieve', this.#handleRetrieve);

    document.body.appendChild(this.listbox);
  }

  remove(): void {
    this.listbox.remove();
    this.listbox.removeEventListener('suggest', this.#handleSuggest);
    this.listbox.removeEventListener('suggesterror', this.#handleSuggestError);
    this.listbox.removeEventListener('retrieve', this.#handleRetrieve);
  }

  #handleSuggest = (e: MapboxHTMLEvent<AutofillSuggestionResponse>): void => {
    // Manually bubble up the event.
    this.#collection.fire('suggest', e.clone(this.#input));
  };

  #handleSuggestError = (e: MapboxHTMLEvent<Error>): void => {
    // Manually bubble up the event.
    this.#collection.fire('suggesterror', e.clone(this.#input));
  };

  #handleRetrieve = (e: MapboxHTMLEvent<AutofillRetrieveResponse>): void => {
    // Manually bubble up the event.
    this.#collection.fire('retrieve', e.clone(this.#input));

    if (!this.#input) {
      return;
    }

    const featureCollection = e.detail;
    if (
      !featureCollection ||
      !featureCollection.features ||
      !featureCollection.features.length
    ) {
      return;
    }

    fillFormWithFeature(featureCollection.features[0], this.#input);
  };
}

interface AutofillCollectionOptions {
  accessToken?: string;
  options?: Partial<AutofillOptions>;
  theme?: Theme;
}

interface EventTypes<AutofillSuggestionResponse, AutofillRetrieveResponse> {
  /**
   * Fired when the user is typing in the input and provides a list of suggestions.
   *
   * The underlying response from {@link MapboxAutofill} is passed as the event's detail,
   * while the responsible input is passed as the event's target.
   *
   * @event suggest
   * @instance
   * @memberof AutofillCollection
   * @type {AutofillSuggestionResponse}
   * @example
   * ```typescript
   * collection.addEventListener('suggest', (event) => {
   *   const suggestions = event.detail.suggestions;
   *   const inputEl = event.target;
   *   // ...
   * });
   * ```
   */
  suggest: MapboxHTMLEvent<AutofillSuggestionResponse>;
  /**
   * Fired when {@link MapboxAutofill} has errored providing a list of suggestions.
   *
   * The underlying error is passed as the event's detail,
   * while the responsible input is passed as the event's target.
   *
   * @event suggesterror
   * @instance
   * @memberof AutofillCollection
   * @type {Error}
   * @example
   * ```typescript
   * collection.addEventListener('suggesterror', (event) => {
   *   const error = event.detail;
   *   const inputEl = event.target;
   *   // ...
   * });
   * ```
   */
  suggesterror: MapboxHTMLEvent<Error>;
  /**
   * Fired when the user has selected a suggestion, before the form is autofilled.
   *
   * The underlying response from {@link MapboxAutofill} is passed as the event's detail,
   * while the responsible input is passed as the event's target.
   *
   * @event retrieve
   * @instance
   * @memberof AutofillCollection
   * @type {AutofillRetrieveResponse}
   * @example
   * ```typescript
   * autofill.addEventListener('retrieve', (event) => {
   *   const featureCollection = event.detail;
   *   const inputEl = event.target;
   *   // ...
   * });
   * ```
   */
  retrieve: MapboxHTMLEvent<AutofillRetrieveResponse>;
}

type AutofillCollectionType = AutofillCollection<
  AutofillSuggestionResponse,
  AutofillRetrieveResponse
>;

/**
 * Underlying collection object class returned by the {@link autofill} function.
 *
 * @class AutofillCollection
 */
export class AutofillCollection<
  AutofillSuggestionResponse,
  AutofillRetrieveResponse
> extends Evented<
  EventTypes<AutofillSuggestionResponse, AutofillRetrieveResponse>
> {
  instances: AutofillInstance[] = [];
  #currentInputs: HTMLInputElement[];

  #autofill = new MapboxAutofill();

  constructor({ accessToken, options, theme }: AutofillCollectionOptions) {
    super();

    config.autofillSessionEnabled = true;

    this.accessToken = accessToken || config.accessToken;
    options && (this.options = options);
    theme && (this.theme = theme);
    this.update();
  }

  /**
   * The [Mapbox access token](https://docs.mapbox.com/help/glossary/access-token/) to use for all requests.
   *
   * @example
   * ```typescript
   * autofill.accessToken = 'pk.my-mapbox-access-token';
   * ```
   */
  get accessToken(): string {
    return this.#autofill.accessToken;
  }
  set accessToken(newToken: string) {
    this.#autofill.accessToken = newToken;
  }

  #options: Partial<AutofillOptions>;

  /**
   * Options to pass to the underlying {@link MapboxAutofill} interface.
   *
   * @example
   * ```typescript
   * autofill.options = {
   *  language: 'en',
   *  country: 'US',
   * };
   * ```
   */
  get options(): Partial<AutofillOptions> {
    return this.#options;
  }
  set options(newOptions: Partial<AutofillOptions>) {
    this.#options = { ...this.#options, ...newOptions };
    this.instances.forEach((instance) => {
      instance.listbox.options = { ...instance.listbox.options, ...newOptions };
    });
  }

  #theme: Theme;

  /**
   * The {@link Theme} to use for styling the autofill component.
   *
   * @example
   * ```typescript
   * autofill.theme = {
   *   variables: {
   *     colorPrimary: 'myBrandRed'
   *   }
   * };
   * ```
   */
  get theme(): Theme {
    return this.#theme;
  }
  set theme(newTheme: Theme) {
    this.#theme = newTheme;
    this.instances.forEach((instance) => {
      instance.listbox.theme = newTheme;
    });
  }

  /** @section {Methods} */

  /**
   * Updates autofill collection based on the current DOM state.
   * @example
   * ```typescript
   * collection.update();
   * ```
   */
  update(): void {
    // STEP 0: Remove and clean up any existing autofill instances
    this.instances.forEach((instance) => {
      instance.remove();
    });
    // STEP 1: Find the input element(s)
    this.#currentInputs = findAddressInputs();
    // STEP 2: Create a new autofill instance for each input
    this.instances = [];
    this.#currentInputs.forEach((input) => {
      const autofillInstance = new AutofillInstance(
        this,
        input,
        this.#autofill
      );
      autofillInstance.listbox.options = this.options;
      autofillInstance.listbox.theme = this.theme;
      this.instances.push(autofillInstance);
    });
  }

  // TODO: optimize this!
  // Called when content changes.
  #handleObserve = (): void => {
    // TODO: add test to make sure this comparison works
    if (!deepEquals(findAddressInputs(), this.#currentInputs)) {
      this.update();
    }
  };

  #observer = new MutationObserver(this.#handleObserve);

  /**
   * Listen for changes to the DOM, and update autofill instances when autofill-able inputs are added/removed.
   *
   * **IMPORTANT:** For performance reasons, it is recommended to carefully control
   * when this is called and to call {@link AutofillCollection#unobserve} when finished.
   *
   * @example
   * ```typescript
   * collection.observe();
   * ```
   */
  observe(): void {
    // Setup observer handler.
    this.#observer.observe(document, {
      subtree: true,
      childList: true
    });

    this.#handleObserve();
  }

  /**
   * Stop listening for changes to the DOM. This only has an effect if called
   * after {@link AutofillCollection#observe}.
   *
   * @example
   * ```typescript
   * collection.unobserve();
   * ```
   */
  unobserve(): void {
    this.#observer.disconnect();
  }
}

/**
 * Entry point for Mapbox Address Autofill, for use on standard HTML input elements.
 *
 * Compared to {@link MapboxAddressAutofill}, this function automatically attaches
 * to eligible [`<input>`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input/text) elements in-place.
 *
 * You must have a [Mapbox access token](https://www.mapbox.com/help/create-api-access-token/).
 *
 * Eligible inputs must be a descendant of a [`<form>`](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/form) element, and the form
 * must have inputs with proper HTML `autocomplete` attributes. The input itself must be of autocomplete `"street-address"` or `"address-line1""`.
 *
 * If your application works with browser autofill, you may already have this functionality.
 * - [The HTML autocomplete attribute](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/autocomplete)
 * - [Autofill](https://web.dev/learn/forms/autofill/)
 *
 * @param optionsArg
 * @param {string} optionsArg.accessToken - A [Mapbox access token](https://www.mapbox.com/help/create-api-access-token/).
 * @param {Partial<AutofillOptions>} [optionsArg.options] - Options to set on the underlying Autofill API.
 * @example
 * <input type="text" autocomplete="street-address" />
 * <script>
 * mapboxsearch.autofill({
 *   accessToken: 'pk.my.token',
 *   options: { country: 'us' }
 * };
 * </script>
 * @example
 * ```typescript
 * const collection = autofill({
 *   accessToken: 'pk.my.token',
 *   options
 * })
 *
 * myClientSideRouter.on('route', () => collection.update());
 * ```
 */
export function autofill(
  optionsArg: AutofillCollectionOptions
): AutofillCollectionType {
  const { accessToken, options } = optionsArg;
  return new AutofillCollection({
    accessToken,
    options
  });
}
import {
  AutofillFeatureSuggestion,
  featureToSuggestion
} from '@mapbox/search-js-core';
import { MapboxSearchListboxAutofillType } from './components/MapboxAddressAutofill';
import {
  AddressConfirmOptions,
  MapboxAddressConfirmation
} from './components/MapboxAddressConfirmation';
import { config } from './config';

import {
  featureToAutofillValueMap,
  findAddressInputs,
  getFormAutofillValues,
  checkAutofillValuesChanged,
  parseFormStructure,
  setFormAutofillValues
} from './utils/autofill';
import { AddressConfirmShowResult } from './utils/confirmation';

const confirmation = new MapboxAddressConfirmation();

/**
 * A utility that can be run prior to form submission that allows a user to correct or confirm an address.
 *
 * This parses and compares an address entered into form fields with the closest address suggestion from the Mapbox Autofill API.
 * Unless an exact match or a custom comparison callback evaluates to true, the user will be shown a modal dialog
 * asking if they would like to use the suggested address.
 *
 * When a suggested address is accepted, the values are automatically updated in the form fields.
 * @param form - HTML form that includes the autocomplete-compliant input fields
 * @param optionsArg - {@link AddressConfirmOptions} Object defining options for Autofill API, UI, form parsing, and address comparison
 * @returns A promise resolving with a result object indicating the decision made by the user
 * @example
 * ```typescript
 * form.addEventListener("submit", async (e) => {
 *   e.preventDefault();
 *   const result = await confirmAddress(form, {
 *     minimap: true,
 *     skipConfirmModal: (feature) =>
 *       ['exact', 'high'].includes(
 *         feature.properties.match_code.confidence
 *       )
 *   });
 *   if (result.type === 'nochange') submitForm();
 * });
 * ```
 */
export async function confirmAddress(
  form: HTMLFormElement,
  optionsArg: AddressConfirmOptions = {}
): Promise<AddressConfirmShowResult> {
  const { sections = [] } = optionsArg;

  if (!confirmation.parentNode) {
    document.body.appendChild(confirmation);
  }

  let collectedResult: AddressConfirmShowResult = { type: 'nochange' };

  const inputs = findAddressInputs(form);
  const structure = parseFormStructure(form);

  const listboxComponents = Array.from(
    document.querySelectorAll<MapboxSearchListboxAutofillType>(
      'mapbox-search-listbox'
    )
  );

  for (const input of inputs) {
    // If we have sections, filter by the form structure.
    if (sections.length) {
      const structureRef = structure.find((s) => s.input === input);
      if (!structureRef) {
        continue;
      }

      if (!sections.includes(structureRef.section)) {
        continue;
      }
    }

    // Get the form's new autofill values to compare against.
    const autofillValues = getFormAutofillValues(form, input);

    // Compare form values against the latest Autofill snapshot (if any);
    // If form values have not changed since last snapshot, skip showing confirmation.
    const listbox = listboxComponents.find((lb) => lb.input === input);
    if (listbox) {
      const lastRetrievedFeature =
        listbox.retrieveFeature as AutofillFeatureSuggestion;
      if (lastRetrievedFeature) {
        const snapshot = featureToAutofillValueMap(lastRetrievedFeature);
        if (!checkAutofillValuesChanged(autofillValues, snapshot)) {
          continue;
        }
      }
    }

    // Show the confirmation with the first feature.
    const accessToken = optionsArg.accessToken || config.accessToken;
    const result = await confirmation.tryShow(autofillValues, {
      ...optionsArg,
      accessToken
    });

    // If changed, autofill the form.
    if (result.type === 'change') {
      input.dataset['mapboxSuccess'] = 'true';
      const suggestion = featureToSuggestion(result.feature);
      setFormAutofillValues(form, input, suggestion);
      if (listbox) {
        /**
         * Imperatively update feature snapshot on listbox to keep it in sync with
         * feature used by confirmation process to update form value.
         */
        listbox.retrieveFeature = result.feature;
      }
    }

    // Set to changed if changed and not cancelled.
    if (result.type === 'change' && collectedResult.type !== 'cancel') {
      collectedResult = result;
    }

    // Set to cancelled if cancelled.
    if (result.type === 'cancel') {
      collectedResult = result;
    }
  }

  return collectedResult;
}
